<template>
  <div id="mapContainer"></div>
  <div id="error" style="display: none">Dieses Unternehmen hat leider keine Koordinaten angegeben, deshalb kann keine Karte angezeigt werden</div>
</template>
  
<script>
import "leaflet/dist/leaflet.css";
import L from "leaflet";

export default {
  name: 'MapProvider',
  props: ['positions'],
  computed: {
    standardizedPositions() {
      if (this.$props.positions === null) {
        return [[0, 0]]
      }
      if (typeof this.$props.positions[0] === 'object') return this.$props.positions
      else return [[...this.$props.positions]]
    },
    center() {
      if (this.standardizedPositions.length === 1) return this.standardizedPositions[0]
      else return [48.9352830, 9.2627305]
    },
  },
  mounted() {
    console.log("Started map provider with the center " + this.center + " and the positions", this.standardizedPositions)
    if (JSON.stringify(this.standardizedPositions) !== JSON.stringify([[0, 0]])) {
      const mapDiv = L.map("mapContainer").setView(this.center, 19);
      L.tileLayer(process.env.VUE_APP_MAP_PROVIDER_URL, {
        attribution: process.env.VUE_APP_MAP_PROVIDER_ATTRIBUTION,
        apikey: process.env.VUE_APP_MAP_PROVIDER_TOKEN,
        maxZoom: 22
      }).addTo(mapDiv);
      let schillerandoIcon = L.icon({
        iconUrl: 'data:image/png;base64,',
        iconSize: [50, 50],
      });
      for (let i = 0; i < this.standardizedPositions.length; i++) {
        L.marker(this.standardizedPositions[i], { icon: schillerandoIcon }).addTo(mapDiv);
      }
    } else {
      document.getElementById("mapContainer").style.display = 'none';
      document.getElementById("error").style.display = 'block';
      console.log("Illegal positions-data")
    }
  },
  methods: {

  },
};
</script>
  
<style scoped>
#mapContainer {
  width: 100%;
  height: 100%;
}
</style>
